import HeaderActionTypes from './header.types';

const INITIAL_STATE={
    blackImage:true
};

const headerReducer=(state = INITIAL_STATE, action) => {
    switch(action.type){
        case HeaderActionTypes.SCROLLING_CHANGES:
            return {
              ...state,
              blackImage: !state.blackImage
            };
        default:
            return state;
    }
}

export default headerReducer;
